import React ,{useState} from "react";

import listItems from "./listitems.jsx";

function implimentnotes()
{
  <listItems/>
}

function App() {

    const [ tonote , changevalue]=useState("");


    const arr=[""];
    function handlechange(event)
    {
      const {value}=event.target;
      changevalue(value);
      arr=[""];

    }


    function implimentnotes(gotnotes)
    {
        const {likhe}=gotsnotes
      return(
        <listItems itemsf={gotsnotes} />

      );

    }







    function additem()
    {


      arr.push(tonote);




    }
















  return (
    <div className="container">
      <div className="heading">
        <h1>To-Do List</h1>
      </div>
      <div className="form">
        <input onChange={handlechange} type="text"  name="toname"  value={tonote} />
        <button >
          <span onClick={additem}>Add</span>
        </button>
      </div>
      {arr.map(implimentnotes)}

    </div>
  );
}

export default App;
