import React ,{useState} from "react";

import listitems from "./listitems";

function implimentnotes(mynotes)
{
  <listitems itemsf= {mynotes[0]} />



}

function App() {

    const [ tonote , changevalue]=useState("");

    function handlechange(event)
    {
      const {value}=event.target;
      changevalue(value);

    }


    const [itemfills , changefill]=useState("");


    const arr=[""];

    function additem()
    {

      changefill(tonote);
      arr.push(tonote);

    }
















  return (
    <div className="container">
      <div className="heading">
        <h1>To-Do List</h1>
      </div>
      <div className="form">
        <input onChange={handlechange} type="text"  name="toname"  value={tonote} />
        <button >
          <span onClick={additem}>Add</span>
        </button>
      </div>
      {arr.map(implimentnotes)}

    </div>
  );
}

export default App;
