import React, { useState } from "react";

function App() {
  const [contact, setContact] = useState({
    fName: "",
    lName: "",
    email: ""
  });


function changehandle(event)
{


  const newvalue=event.target.value;
  const inputname=event.target.name;

  setContact(prevvalue=>
    {
      if(newvalue=="fName")
      {
        return
        {
          fName=newvalue,
          lName=prevvalue.lName,
          email=prevvalue.email
        }
      }
      if(newvalue=="lName")
      {
        return
        {
          lName=newvalue,
          fName=prevvalue.fName,
          email=prevvalue.email
        }
      }

      if(newvalue=="lName")
      {
        return
        {
          lName=newvalue,
          fName=prevvalue.fName,
          email=prevvalue.email
        }
      }



    }

    );







}



  return (
    <div className="container">
      <h1>
        Hello {contact.fName} {contact.lName}
      </h1>
      <p>{contact.email}</p>
      <form>
        <input name="fName" placeholder="First Name"  onChange={changehandle}/>
        <input name="lName" placeholder="Last Name" onChange={changehandle}  />
        <input name="email" placeholder="Email" onChange={changehandle} />
        <button>Submit</button>
      </form>
    </div>
  );
}

export default App;
