    }



  return (
    <div className="container">
      <div className="heading">
        <h1>To-Do List</h1>
      </div>
      <div className="form">
        <input onChange={handlechange} type="text"  name="toname"  value={tonote} />
        <button >
          <span  onClick={additem}>Add</span>
        </button>
      </div>
        <ul>
        {items.map((todo)=> {
          return <li>{todo}</li>;
        })}

        </ul>

    </div>
  );
}

export default App;
