import React,{useState} from "react";

function App() {

  const [name, setname]=useState("");

  function handleChange(event)
  {

  setname(event.target.value)

  } 

  const [update_hell, sethello]=useState("");

  function whenclicked()
  {
  sethello(" " +name);

  }

  const [overbutton, setoverutton]=useState(false);

  function handleout()
  {
  setoverutton(false);

  }

  function handleover()
  {
  setoverutton(true);

  }

///controlled conmponents


  return (
    <div className="container">
      <h1>Hello{update_hell}</h1>
      <input
       onChange={handleChange}
       type="text"
        placeholder="What's your name?"
        value={name}
         />
      <button  onClick={whenclicked}
       style={{backgroundColor: overbutton? "black" : "white"}}

       onMouseOver={handleover}
       onMouseOut={handleout}


      >Submit</button>
    </div>
  );
}

export default App;
