import React,{useState} from "react";

 function ToDoItem(props)
{ 
    const [isDone , setisdone] =useState(false); 
    function handleclick()
    {
        setisdone((prevvalue)=>
        {
            return !prevvalue;

        }
        );

    }


    return(
        <div  onClick={handleclick}>

        <li style={{textDecoration: isDone? "line-through": "none"}}>{props.text}</li>
        </div>
       

    );





