import React,{useState} from "react";

function App() {

  const [firstname,setnewFirst]=useState("");

  const [secondname,setnewSecond]=useState("");


  function input1(event)
  {
    setnewFirst(event.target.value)


  }

  function input2(event)
  {
    setnewSecond(event.target.value)

  }


  return (
    <div className="container">
      <h1>Hello {firstname} {secondname}</h1>
      <form>
        <input name="fName" placeholder="First Name" onChange={input1} />
        <input name="lName" placeholder="Last Name"  onChange={input2} />
        <button>Submit</button>
      </form>
    </div>
  );
}

export default App;
