     }

    );







}



  return (
    <div className="container">
      <h1>
        Hello {contact.fName} {contact.lName}
      </h1>
      <p>{contact.email}</p>
      <form>
        <input name="fName" placeholder="First Name"  onChange={changehandle} value={contact.fName}/>
        <input name="lName" placeholder="Last Name" onChange={changehandle}  value={contact.lName} />
        <input name="email" placeholder="Email" onChange={changehandle}   value={contact.email}/>
        <button>Submit</button>
      </form>
    </div>
  );
}

export default App;
