import React, { useState } from "react";

function App() {
    const [contact, setContact] = useState({
    fName: "",
    lName: "",
    email: ""
  });


function changehandle(event)
{


  const newValue=event.target.value;
  const inputName=event.target.name;

  // const {value ,name}=event.target;

  setContact((prevValue)=>
    {
      if(inputname==="fName")
      {
        return{
          fName : newValue,
          lName : prevValue.lName,
          email : prevValue.email
        };
      }


      if(inputName==="lName")
      {
        return{
          lName : newValue,
          fName : prevValue.fName,
          email : prevValue.email
        };
      }

      if(inputName==="email")
      {
        return{
          lName : prevValue.lName,
          fName : prevValue.fName,
          email : newValue
        };
      }



     }

    );







}



  return (
    <div className="container">
      <h1>
        Hello {contact.fName} {contact.lName}
      </h1>
      <p>{contact.email}</p>
      <form>
        <input name="fName" placeholder="First Name"  onChange={changehandle} value={contact.fName}/>
        <input name="lName" placeholder="Last Name" onChange={changehandle}  value={contact.lName} />
        <input name="email" placeholder="Email" onChange={changehandle}   value={contact.email}/>
        <button>Submit</button>
      </form>
    </div>
  );
}

export default App;
